generated.css:
*{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    height: 100%;
}
::-webkit-scrollbar {
    display: none;
}

body{
    display: flex;
    height: 100vh;
    width: 100%;
    flex-direction: column;
}

.codeWrapper , .pikaWrapper {
    height: 50%;
    flex: 1 ;
    width: 100%;
}

.codeWrapper{
    padding: 10px;
}
.pikaWrapper {
    overflow: hidden;
}
#code{
    overflow: hidden;
    height: 100%;
}

.buttons{
    position: absolute;
    right: 0;
    top: 0;
    display: flex;
    flex-direction: column;
    height: 50vh;
}
.buttons>button{
    margin: 10px;
    background: #ddd;
    padding: 8px 10px;
    border: none;
}
.buttons>button.active{
    box-shadow: 1px 1px 1px rgba(0,0,0,0.8);
}
.buttons>button:focus{
    outline: none;
}
